import styled, { css } from "styled-components";

export const TestimonialContainer = styled.div`
  width: 100%;
  align-items: center;
  background: var(--primary-white);
  padding: 0 8px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const TestimonialSessionsContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  margin: 72px 0px;
  @media only screen and (min-width: 1024px) {
    margin: 112px 0px;
  }
`;
export const SectionHeader = styled.div`
  font-size: var(--header-sm);
  font-family: var(--Header-font-family);
  line-height: 130%;
  font-weight: var(--header-weight);
  color: var(--primary-white);
  margin-bottom: 8px;
  text-align: center;
  width: 100%;
  padding: 0 8px;

  ${(props) =>
    props.headerblackcolor &&
    css`
      color: var(--header-text-color);
    `}
  @media only screen and (min-width: 1024px) {
    font-size: var(--header-lg);
    width: 60%;
    padding: 0;
    ${(props) =>
    props.headerwidth &&
    css`
      width: 80%;
    `}
  }
`;

export const SectionHeaderKeyWord = styled.span`
  color: var(--primary-color);
  ${(props) =>
    props.darkmodeprimcolor &&
    css`
      color: var(--primary-light-gradient-color);
    `}
`;

export const SectionDescription = styled.div`
  font-size: var(--body-lg);
  font-family: var(--Body-font-family);
  line-height: 130%;
  font-weight: var(--body-regular-weight);
  color: var(--primary-white);
  margin-bottom: 40px;
  width: 100%;
  text-align: center;
  ${(props) =>
    props.descriptionblackcolor &&
    css`
      color: var(--header-text-color);
    `}
  @media only screen and (min-width: 1024px) {
    font-size: var(--body-xl);
    width: 70%;
  }
`;

export const VideoDemoContainer = styled.div`
  width:90%;
  height: 100%;
  margin-bottom: 32px;
  overflow: hidden;

  video {
    width: 100%;
    height: 100%;
    border-radius:6px;
  }
  @media only screen and (min-width: 1024px) {
    margin-bottom: 40px;
    width: 70%;
    height: 100%;
    video {
      width: 100%;
      height: 100%;
      border-radius:6px;
    }
  }
`;
