import styled, { css } from "styled-components";

export const IntroContainer = styled.div`
  align-items: center;
  height: calc(100vh - 64px);
  background: var(--primary-color);
  padding: 0 8px;
  display: flex;
  align-items: center;
  justify-content: center;


  @media only screen and (min-width: 1024px) {
    padding: 0 100px;
  }
`;

export const IntroSessionsContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  border: 1px solid red;
  height: 100%;
  width: 100%;
  @media only screen and (min-width: 1024px) {
    flex-direction: row;
    justify-content: space-between;
  }
`;
export const IntroSessionsHalfContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
  width: 50%;
  height: 100%;
  border: 1px solid yellow;
`


export const IntoHeader = styled.div`
  font-size: var(--header-sm);
  font-family: var(----Header-font-family);
  line-height: 110%;
  font-weight: var(--header-weight);
  color: var(--primary-white);
  margin-bottom: 16px;
  width: 100%;
  ${(props) =>
    props.headerblackcolor &&
    css`
      color: var(--header-text-color);
    `}
    @media only screen and (max-width: 320px) {
    font-size: var(--header-xs);
  }
  @media only screen and (min-width: 1024px) {
    font-size: var(--header-lg);
    width: 100%;
  }
`;
export const IntoDescription = styled.div`
  font-size: var(--body-lg);
  font-family: var(--Body-font-family);
  line-height: 130%;
  font-weight: var(--body-regular-weight);
  color: var(--primary-white);
  margin-bottom: 40px;
  width: 100%;

  @media only screen and (min-width: 1024px) {
    font-size: var(--body-xl);
    width: 70%;
  }
`;

export const IntroLogoContainer = styled.div`
  width: 100px;
  height: 100%;
  margin-bottom: 24px;
  @media only screen and (max-width: 320px) {
    margin-bottom: 0px;
    width: 50px;
    height: 100%;
  }
  @media only screen and (min-width: 1024px) {
    margin-bottom: 40px;
  }
`;
