import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
//components
import { Button } from "rsuite";

//styled
import {
  ContactSectionContentWrapper,
  ContactSectionContentBottomWrapper,
  ContactSectionInputWrapper,
  ContactSectionFormContent,
  ContactSectionButtonWrapper,
  SectionHeaderKeyWord,
  SectionHeader,
} from "./ContactStyle";

const ContactContent = () => {
  const form = useRef();
  const [sendMsg, setSendMsg] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "q0_gmail_service_id",
        "q0_contact_us_template",
        form.current,
        "user_7fcX0YN0dqD6XDDHMDheu"
      )
      .then(
        (result) => {
          setSendMsg(true);
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
    console.log("The email has been sent!");
  };

  return (
    <ContactSectionContentWrapper>
      <SectionHeader headerblackcolor>
        <SectionHeaderKeyWord>Ask</SectionHeaderKeyWord> us anything
      </SectionHeader>
      <ContactSectionContentBottomWrapper>
        {sendMsg ? (
          <div className="send-confimation">
            <div className="send-confimation-message">
              <p>Your message has been sent. We will get back to you as soon as possible.</p>
            </div>

           <ContactSectionButtonWrapper>
              <Button
                type="button"
                onClick={() => setSendMsg(false)}
                appearance="primary"
              >
                Ok
              </Button>
            </ContactSectionButtonWrapper>
          </div>
        ) : (
          <form ref={form} onSubmit={sendEmail}>
            <ContactSectionFormContent>
              <ContactSectionInputWrapper>
                <label>Name</label>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter your name here"
                  required
                  className="name-input"
                />
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  placeholder="Enter your email here"
                  required
                />
              </ContactSectionInputWrapper>
              <ContactSectionInputWrapper formmargin>
                <label>Message</label>
                <textarea
                  name="message"
                  placeholder="Enter your message here"
                  required
                />
              </ContactSectionInputWrapper>
            </ContactSectionFormContent>
            <ContactSectionButtonWrapper>
              <Button type="submit" appearance="primary">
                Submit
              </Button>
            </ContactSectionButtonWrapper>
          </form>
        )}
      </ContactSectionContentBottomWrapper>
    </ContactSectionContentWrapper>
  );
};

export default ContactUsContent;
